/*SCRIPT PARA CRIAR A ESTRUTURA INICIAL DE NOSSO BANCO DE DADOS ORIGEM*/

CREATE TABLE IF NOT EXISTS TB_SALES_HEADERS(SALE_ID       BIGINT   NOT NULL,
 											CUSTOMER_ID   BIGINT   NOT NULL,
 											VENDOR_ID     BIGINT   NOT NULL,
                                            DELIVERY_DATE DATETIME NOT NULL,
										    BILLING_DATE  DATETIME NOT NULL,
                                            PAYMENT_MODE  BIGINT   NOT NULL,
                                            CREATED_AT    DATETIME NOT NULL,
                                            CREATED_BY    BIGINT   NOT NULL,
                                            UPDATED_AT    DATETIME,
                                            UPDATED_BY    BIGINT,
                                            PRIMARY KEY(SALE_ID)
                                            );
                                            
CREATE TABLE IF NOT EXISTS TB_SALES_LINES(SALE_LINE_ID   BIGINT   	  NOT NULL,
                                          SALE_HEADER_ID BIGINT  	  NOT NULL,
										  ITEM_ID        BIGINT   	  NOT NULL,
										  QUANTITY_PACK  BIGINT   	  NOT NULL,
                                          QUANTITY_UNIT  BIGINT   	  NOT NULL,
									      UNIT_VALUE     DOUBLE   	  NOT NULL,
                                          OBSERVATIONS   VARCHAR(500) NOT NULL,
                                          CREATED_AT     DATETIME 	  NOT NULL,
                                          CREATED_BY     BIGINT   	  NOT NULL,
                                          UPDATED_AT     DATETIME,
                                          UPDATED_BY     BIGINT,
                                          PRIMARY KEY(SALE_LINE_ID)
                                          );
                                          
CREATE TABLE IF NOT EXISTS TB_SALES_ITEMS(ITEM_ID          BIGINT   NOT NULL,
                                          ITEM_DESCRIPTION VARCHAR(500)  	NOT NULL,
                                          UNIT_BY_PACK     BIGINT   NOT NULL,
									      UNIT_VALUE       DOUBLE   NOT NULL,
                                          CREATED_AT       DATETIME NOT NULL,
                                          CREATED_BY       BIGINT   NOT NULL,
                                          UPDATED_AT       DATETIME,
                                          UPDATED_BY       BIGINT,
                                          PRIMARY KEY(ITEM_ID)
                                          );
                                          
 CREATE TABLE IF NOT EXISTS TB_SALES_CUSTOMERS(CUSTOMER_ID   BIGINT   NOT NULL,
		                                       CUSTOMER_NAME VARCHAR(1000)   NOT NULL,
		                                       CREATED_AT    DATETIME NOT NULL,
		                                       CREATED_BY    BIGINT   NOT NULL,
		                                       UPDATED_AT    DATETIME,
		                                       UPDATED_BY    BIGINT,
                                               PRIMARY KEY(CUSTOMER_ID)
		                                       );                               

 CREATE TABLE IF NOT EXISTS TB_SALES_VENDORS(VENDOR_ID   BIGINT   NOT NULL,
		                                     VENDOR_NAME VARCHAR(1000) NOT NULL,
		                                     CREATED_AT  DATETIME NOT NULL,
		                                     CREATED_BY  BIGINT   NOT NULL,
		                                     UPDATED_AT  DATETIME,
		                                     UPDATED_BY  BIGINT,
                                             PRIMARY KEY(VENDOR_ID)
		                                     );       
		                                     
